import React, {createContext, useContext, useState} from "react";
export const ChatPreviewContext = createContext();


export const ChatPreviewContextProvider = ({children})=> {
    const [receipent, setReceipent] = useState(["Sarang Thakare", "Anurag Prasad", "Anshu Kumar", "Laddu Kumar"]);
    const [messages, setMessages] = useState(()=> {
        receipent.reduce((allChats, individualChats)=>{
            allChats[individualChats]=["All messages are end-to-end encrypted"];
            return allChats;
        },{})
    });
    const addNewMessage = (msg, curReceipent)=>{
        setMessages((prevMessages)=>({
            ...prevMessages, [curReceipent]:[...prevMessages[curReceipent], msg]
        }));
    }
    return (
        <ChatPreviewContext.Provider value={{receipent, setReceipent}}>
            {children}
        </ChatPreviewContext.Provider>
    );
}

const {recipent, setReceipent, messages, addNewMessage}=useContext(ChatPreviewContext);
