import React from "react";
import sendIcon from "../../assets/images/sendIcon.png"

const MessageInput = () => {
    // return (
    //     <div className={"flex flex-row"}>
    //     <div className={""}>
    //         <input type="text" placeholder="Type a message" 
    //             className={"flex-grow focus:outline-none bg-black placeholder-white text-white rounded-md h-[50px] mb-0"}/>
    //     </div>
    //     <button className={"w-[60px] p-2 rounded-full border-2 border-black bg-[#578E7E] pb-2 mb-2"}>
    //         <img src={sendIcon}  alt="Send" className={"rounded-full h-[30px]"}/></button>
    //     </div>
    // );
    <div className="fixed bottom-0 w-full bg-gray-100 p-2 border-t border-gray-300">
    <div className="flex items-center gap-2">
      {/* Left Icon */}
      <button className="text-gray-600 hover:text-gray-800">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M4 7v4m0 0v4m0-4h16"
          />
        </svg>
      </button>

      {/* Text Input */}
      <input
        type="text"
        placeholder="Type a message..."
        className="flex-grow px-4 py-2 bg-white border border-gray-300 rounded-full focus:outline-none focus:ring focus:ring-blue-300"
      />

      {/* Right Icon */}
      <button className="text-blue-600 hover:text-blue-800">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M14 5l7 7m0 0l-7 7m7-7H3"
          />
        </svg>
      </button>
    </div>
  </div>
}

export default MessageInput;


