import React, {useState} from "react";
import SearchBar from "./searchbar";
import ChatPreview from "./ChatPreview";

const Panel = () => {
    const [panel, togglePanel] = useState(1);

    return (
        <div className={`${panel ? 'w-[380px]': 'w-0'} transition-all ease-in-out duration-200 bg-[#3D3B40] p-1 overflow-hidden`}>
            <div className="flex flex-row gap-3">
                <div className={"flex flex-col"}>
                    <button onClick={()=>{togglePanel(!panel)}}>
                    <div className={"w-7 h-0.5 bg-white mb-1"}></div>
                    <div className={"w-7 h-0.5 bg-white mb-1"}></div>
                    <div className={"w-7 h-0.5 bg-white mb-1"}></div>
                    </button>
                    <div className={"bg-[#3D3B40] text-white p-2"}>Chats</div>
                </div>
            </div>
            <SearchBar/>
            <ChatPreview title="Anurag Prasad"/>
            <ChatPreview tit/>
        </div>
    );
}
export default Panel;
