import React, { useState } from 'react';
import './App.css';
function App() {
  const [isOpen, setIsOpen] = useState(false); // Sidebar open/close state

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div
        className={`transition-all duration-300 ease-in-out bg-gray-800 text-white p-4 fixed md:relative ${
          isOpen ? 'w-64' : 'w-0'
        } h-full overflow-hidden z-50 md:w-64 md:static`}
      >
        {/* Close Button */}
        <button
          onClick={() => setIsOpen(!isOpen)} // Toggle sidebar state
          className="absolute top-4 left-4 md:hidden p-2 bg-blue-500 rounded-full"
        >
          <div className="w-6 h-1 bg-green mb-1"></div>
          <div className="w-6 h-1 bg-white mb-1"></div>
          <div className="w-6 h-1 bg-white mb-1"></div>
        </button>
        
        <div className="mt-16">
          <ul>
            <li className="py-2">Home</li>
            <li className="py-2">About</li>
            <li className="py-2">Services</li>
            <li className="py-2">Contact</li>
          </ul>
        </div>
      </div>

      {/* Main content area */}
      <div className="flex-1 p-8 ml-0 transition-all duration-300 ease-in-out">
        <h1 className="text-3xl">Main Content</h1>
        <p className="mt-4">This is the main content area. The sidebar can be toggled by clicking the hamburger icon.</p>
      </div>
    </div>
  );
}

export default App;
