import React from "react";
import sendIcon from "../../assets/images/sendIcon.png"

const MessageInput = () => {
    return (
        <div className={"flex flex-row w-[100%]"}>
        
        <input type="text" placeholder="Type a message..." 
                className={"focus:outline-none bg-black placeholder-white text-white rounded-md h-[50px] mb-0 border-t-5 border-t-white pl-5"}/>
        
        <button className={"w-[60px] p-2 rounded-full border-2 border-black bg-[#578E7E] pr-3 mb-2"}>
            <img src={sendIcon}  alt="Send" className={"rounded-full h-[30px]"}/></button>
        </div>
    );
}

export default MessageInput;
