import React, {createContext, useContext, useState} from "react";
export const ChatPreviewContext = createContext();


export const ChatPreviewContextProvider = ({children})=> {
    const [recipient, setRecipent] = useState(["Sarang Thakare", "Anurag Prasad", "Anshu Kumar", "Laddu Kumar"]);
    const [messages, setMessages] = useState(()=> {
        return recipient.reduce((allChats, individualChats)=>{
            allChats[individualChats]=["All messages are end-to-end encrypted"];
            return allChats;
        },{})
    });
    const addNewMessage = (msg, curRecipient)=>{
        setMessages((prevMessages)=>({
            ...prevMessages, [curReceipent]:[...prevMessages[curRecipient], msg]
        }));
    }
    return (
        <ChatPreviewContext.Provider value={{recipient, setRecipient}}>
            {children}
        </ChatPreviewContext.Provider>
    );
}

export const useChats = ()=> {
    const {receipent, setReceipent, messages, addNewMessage}=useContext(ChatPreviewContext);
    return {receipent, setReceipent, messages, addNewMessage};
}
